INSERT INTO `taxonomy` (`taxa_id`, `taxa_name`, `taxa_description`) VALUES
(1, 'Virginia Woolf', 'A British modernist author, born in 1882 in London. \r\nBorn Virginia Stephen, she married Leonard Woolf in 1912 and became a a significant figure in London literary society and a central figure in the influential Bloomsbury Group of intellectuals. Her most famous works include the novels Mrs Dalloway (1925), To the Lighthouse (1927) and Orlando (1928), and the book-length essay A Room of One''s Own (1929), with its famous dictum, "A woman must have money and a room of her own if she is to write fiction." Woolf suffered from severe bouts of mental illness throughout her life, thought to have been the result of what is now termed bipolar disorder,[1] and committed suicide by drowning in 1941 at the age of 59.'),
(2, 'Orlando: A Biography', 'A novel by Virginia Woolf first published in 1928 by Hogarth Press in London and Harcourt, Brace & co. in New York. \r\n\r\nCentered around the eponymous protagonist Orlando, the novel spans four centuries, and is heavily based on Vita Sackville-West and her family history. '),
(3, 'British Edition', 'Editions of Orlando printed in Britain'),
(4, 'American Edition', 'Editions of Orlando printed in the U.S.'),
(5, 'Type proofs', 'Type proofs from R&R Clark, corrected by Woolf Jun 9th-July 22nd 1928. Sent to Crosby Gaige for printing of limited edition, and subsequently used for Harcourt Brace & Co., regular edition. '),
(8, 'First US Trade Edition', 'The first American edition of Woolf''s Orlando, published by Harcourt, Brace & co., on October 18th, 1928. This edition followed a limited special edition from Crosby & Gaige published on Oct 2nd, 1928, and was printed from the same plates.  '),
(9, 'First British Edition', 'The first British edition of Woolf''s Orlando, published by Hogarth Press on October 11th, 1928. ');
